import Icons from "../../Global/Icons"
export default function Stories(){
    const stories = {
        user: ["9gag", "meowed", "barked", "nathanwpylestrangeplanet", "wawawicomics","respondeai", "filomoderna", "memeriagourmet"],
        src: []
    }
    for(let i = 0; i < stories[0].length;i++){
        stories.src.push({source: `assets/img/${user[i]}.svg`})
    }
    console.log(stories.src)
    return(
        <div class="stories">
            {stories.map( props =>
                <Story user={props.user} sourcer={props.src} />
            )}
            <div class="setinha">
              <Icons name="chevron-forward-circle" />
            </div>
        </div>
    )
}
function Story(props){
    return (
        <div class="story">
                <div class="imagem">
                    <img src={props.source} />
                </div>
                <div class="usuario">
                {props.user}
                </div>
        </div>
    )
}
